import { makeStyles } from "@material-ui/core/styles";

export default makeStyles((theme) => ({
  container: {
    zIndex: 1,
  },
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  hero: {
    height: '600px',
    backgroundSize: '100%',
    width: '100%',
    position: 'relative',
  },

  heroContainer: {
      maxWidth: '60%',
      margin: 'auto',
  },
  heroContent: {
      height: '600px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
  }
}));
