// @ts-nocheck
import React, {useState} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import SearchIcon from '@material-ui/icons/Search';
import MoreIcon from '@material-ui/icons/MoreVert';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import Switch from '@material-ui/core/Switch';
import useStyles from './styles';
import "@fontsource/roboto"; // Loading Roboto font. Material-UI was designed with this font in mind.
import {
  ThemeProvider,
  CardHeader,
  CardContent,
  Container,
  CssBaseline,
} from "@material-ui/core";
import { createMuiTheme } from "@material-ui/core/styles";

// Define theme settings
const light = {
  palette: {
    type: "light",
  },
};

const dark = {
  palette: {
    type: "dark",
  },
};

const NavBar = ({input, setInput}) => {
  const classes = useStyles()
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [auth, setAuth] = React.useState(true);
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState<null | HTMLElement>(null);
// start
// check for saved 'darkMode' in localStorage
//let darkMode = localStorage.getItem('darkMode'); 

  let theme;
if (localStorage) {
  theme = localStorage.getItem("theme");
}
if (theme === light || theme === dark) {
  body.classList.add(theme);
} else {
  body.classList.add(lightTheme);
}
const changeTheme = (e) => {
  if (theme === dark) {
    body.classList.replace(darkTheme, lightTheme);
    e.target.classList.remove(clickedClass);
    localStorage.setItem("theme", "light");
    theme = lightTheme;
  } else {
    body.classList.replace(lightTheme, darkTheme);
    e.target.classList.add(clickedClass);
    localStorage.setItem("theme", "dark");
    theme = darkTheme;
  }
};

// test finsished
//const enableDarkMode = () => {
  //localStorage.setItem('theme', 'enabled');
//}

// const disableDarkMode = () => {

  // 2. Update darkMode in localStorage 
 // localStorage.setItem('darkMode', null);
//}

// If the user already visited and enabled darkMode
// start things off with it on


// const changeTheme = (e) => {
//   setIsDarkTheme(!isDarkTheme);
//   darkMode = localStorage.getItem('darkMode'); 
//   if (darkMode !== 'dark') {
//     enableDarkMode();
//   } else {  
//     disableDarkMode(); 
//   }
// };



  const isMenuOpen = Boolean(anchorEl);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setAuth(event.target.checked);
  };

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
  };

  const handleMobileMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setMobileMoreAnchorEl(event.currentTarget);
  };


  const menuId = 'primary-search-account-menu';
  const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={menuId}
      keepMounted
      transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isMenuOpen}
      onClose={handleMenuClose}
    >
      <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
      <MenuItem onClick={handleMenuClose}>My account</MenuItem>

    </Menu>
  );

  const mobileMenuId = 'primary-search-account-menu-mobile';
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <MenuItem>
        <FormGroup>
        <FormControlLabel
          control={<Switch checked={isDarkTheme} onChange={changeTheme} aria-label="login switch" />}
          label={auth ? 'Light' : 'Dark'}
        />
      </FormGroup>
    </MenuItem>
    </Menu>
  );

  return (
    <ThemeProvider
      theme={isDarkTheme ? createMuiTheme(dark) : createMuiTheme(light)}
    >
      <CssBaseline />
      <Container>
            <CardContent>
              <Typography variant="body1">
              </Typography>
            </CardContent>
    <div className={classes.grow}>
      <AppBar position="static" color="default">
        <Toolbar className={classes.root} disableGutters={false}>
          <Typography className={classes.pThick}>
          Unsplash </Typography>
          <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase
              placeholder="Searchâ€¦"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              inputProps={{ 'aria-label': 'search' }}
            />
          </div>
          <div className={classes.grow} />
          <div className={classes.sectionDesktop}>
          <CardHeader
              action={
                <FormGroup>
                  <FormControlLabel
                  id="dark-mode-toggle"
                  class="dark-mode-toggle"
                    control={
                      <Switch checked={isDarkTheme} onChange={changeTheme} />
                    }
                    label="Dark Theme"
                  />
                </FormGroup>
              }
            />
          </div>
          <div className={classes.sectionMobile}>
            <IconButton
              aria-label="show more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MoreIcon />
            </IconButton>

          </div>
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
      {renderMenu}
    </div>
   </Container>
   </ThemeProvider>
  );
}
export default NavBar;
