import { makeStyles } from "@material-ui/core/styles";

export default makeStyles((theme) => ({
  container: {
    zIndex: 1,
  },
  hero: {
    height: '60%',
    backgroundSize: '100%',
    width: '100%',
    position: 'relative',
  },
  media: {
    height: 10,
    paddingTop: '56.25%', // 16:9
  },
  heroContainer: {
      maxWidth: '60%',
      margin: 'auto',
  },
  heroContent: {
      height: '600px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
  }
}));
